import React, { useState } from 'react';
import './App.css';
import Preview from'./components/Preview';
import Message from './components/message';
import NotesContainer from './components/Notes/NotesContainer';
import NoteList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {
   const [notes,setNotes] = useState([]);
   const [title,setTitle] = useState ('');
   const [content,setContent] = useState ('');
   const [selectednote,setSelectednote] = useState(null);
   const [creating,setCreating] = useState(false);
   const [editing,setEditing] =useState(false);
//  تغيير عنوان الملاح
const changeTitleHandler =(event) => {
  setTitle(event.target.value);
}
// تغيير محتوى الملاحضة

const changeContentHandler =(event) => {
  setContent(event.target.value);
} 
//حفظ الملاحضة
const saveNoteHandler = () =>{
    const note ={
    id : new Date(),
    title :title ,
    content : content
  }

  const updateNotes=[...notes,note];

  setNotes(updateNotes);
  setCreating(false);
  setSelectednote(note.id);

  setTitle('');
  setContent('');
}

const selectNoteHandler = noteId =>{
  setSelectednote(noteId);
}

  const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if(notes.length === 0){
      return <Message title="لا يوجد ملاحضة"/>
    }

    if(!selectednote){
      return <Message title="الرجاءاختيار ملاحضة"/>
     }

     const note =notes.find(note => {
      return note.id === selectednote;
     });

    return (
      <div>
        <div className="note-operations">
          <a href="#">
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        <div>
          <h2>{note.title}</h2>
          <p>{note.content}</p> 
        </div>
      </div>
    );
  };

  const addNoteHandler =() => {
    setCreating(true);
  }
  return (
    <div className="App">
      <NotesContainer>
       <NoteList>
          {notes.map(note => <Note key={note.id} title={note.title} noteClicked={() => selectNoteHandler(note.id)}/>)}
        </NoteList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
      {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
