import React, { useState } from 'react';
import './App.css';
import Preview from'./components/Preview';
import Message from './components/message';
import NotesContainer from './components/Notes/NotesContainer';
import NoteList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {
   const [notes,setNotes] = useState([]);
   const [title,setTitle] = useState ('');
   const [content,setContent] = useState ('');
   const [selectednote,setSelectednote] = useState(null);
   const [creating,setCreating] = useState(false);
   const [editing,setEditing] =useState(false);
//  تغيير عنوان الملاح
const changeTitleHandler =(event) => {
  setTitle(event.target.value);
}
// تغيير محتوى الملاحضة

const changeContentHandler =(event) => {
  setContent(event.target.value);
} 
//حفظ الملاحضة
const saveNoteHandler = () =>{
    const note ={
    id : new Date(),
    title :title ,
    content : content
  }

  const updateNotes=[...notes,note];

  setNotes(updateNotes);
  setCreating(false);
  setSelectednote(note.id);
  setTitle('');
  setContent('');
}
 // اختيار ملاحضة
const selectNoteHandler = noteId =>{
  setSelectednote(noteId);
  setCreating(false);
  setEditing(false);
}

//الانتقال الى وضع التعديل

const editeNotehandler = () =>{
  const note =notes.find(note=> note.id === selectednote);
  setEditing(true);
  setTitle(note.title);
  setContent(note.content);
} 

// تعديل ملاحضة
const updateNoteHandler = ()=>{
   const updatedNotes = [...notes];
   const noteIndex =notes.findIndex(note =>note.id === selectednote);
   updatedNotes[noteIndex] ={
    id: selectednote,
    title: title,
    content: content
   };

setNotes(updatedNotes);
setEditing(false);
setTitle('');
setContent('');

}
//الانتقال الى واجهة اضافة ملاحظة
const addNoteHandler = ()=>{
  setCreating(true);
  setEditing(false);
  setTitle('');
  setContent('');
}

const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if(notes.length === 0){
      return <Message title="لا يوجد ملاحضة"/>
    }

    if(!selectednote){
      return <Message title="الرجاءاختيار ملاحضة"/>
     }

     const note =notes.find(note => {
      return note.id === selectednote;
     });


     let noteDesplay =(
      <div>
        <h1>{note.title}</h1>
        <p>{note.content}</p>
      </div>
     )

     if (editing) 
     {
      noteDesplay=(
      <div>
        <h1>تعديل ملاحضة</h1>
        <form>
        <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

           <a href="#" className="button green" onClick={updateNoteHandler}>
            تعديل
          </a>
        </form>
      </div>
      );
     }

    return (
      <div>
        {!editing &&
          <div className="note-operations">
            <a href="#" onClick={editeNotehandler}>
             <i className="fa fa-pencil-alt" />
            </a>
            <a href="#">
             <i className="fa fa-trash" />
            </a>
        </div>
        }

         {noteDesplay}
      </div>
    );
  };


  return (
    <div className="App">
      <NotesContainer>
       <NoteList>
          {notes.map(note => <Note key={note.id}
           title={note.title} 
           noteClicked={() => selectNoteHandler(note.id)}
           active={selectednote === note.id}
           />)}
        </NoteList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
      {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
